TutorialStepData endTutorial = {};

class TutorialWindow : public Window, public Handlers::OnButtonPressedInterface
{
    Reference<AppCUI::Controls::ImageView> imgView;
    Reference<CanvasViewer> canvasViewer;
    Reference<Label> label1, label2;
    Reference<Button> backButton, nextButton;
    uint32 currentTutorialStep;
    std::vector<std::reference_wrapper<TutorialStepData>> tutorialSteps = { step0_0, step1_1, step1_2, step2_0, step3_0, step4_0, step5_0, endTutorial };

  public:
    TutorialWindow() : Window("GView tutorial", "t:1,l:0,w:74,h:23", WindowFlags::None)
    {
        currentTutorialStep = 0;

        label1 = Factory::Label::Create(this, "", "t:1,l:5,w:60");
        label2 = Factory::Label::Create(this, "You can follow the tutorial using the buttons bellow", "t:2,l:5,w:60");

        backButton = Factory::Button::Create(this, "PreviousStep", "t:4,l:14,w:13", 100, ButtonFlags::Flat);
        backButton->SetEnabled(false);
        backButton->Handlers()->OnButtonPressed = this;
        nextButton                              = Factory::Button::Create(this, "NextStep", "t:4,l:34,w:10", 200, ButtonFlags::Flat);
        nextButton->Handlers()->OnButtonPressed = this;

        Factory::Label::Create(this, "------------------------------------------------------------", "t:5,l:5,w:54");

        LocalString<32> canvasLayout;
        canvasLayout.SetFormat("t:6,l:1,w:%u,h:%u", canvasWidth, canvasHeight);

        canvasViewer = Factory::CanvasViewer::Create(this, canvasLayout.GetText(), canvasWidth, canvasHeight, ViewerFlags::HideScrollBar);
        PrintCurrentStep();
    }

    void PrintCurrentStep()
    {
        label1->SetText("Welcome to the GView tutorial");
        auto canvas = canvasViewer->GetCanvas();
        if (currentTutorialStep <= 3) {
            label2->SetText("You can follow the tutorial using the buttons bellow");
        } else if (currentTutorialStep == 4) {
            label2->SetText("Here you can view each character along with its hex value");
        } else if (currentTutorialStep == 5) {
            label2->SetText("We can further see more data in the Sections menu");
        } else if (currentTutorialStep == 6) {
            label2->SetText("A red bar signifies a search field");
        }else {
            label1->SetText("Welcome to the GView tutorial");
            label2->SetText("Well done, the tutorial has ended!");
            canvas->Clear(' ',{Color::Transparent,Color::DarkBlue});
            return;
        }


        auto stepVal = tutorialSteps[currentTutorialStep].get().begin();

        Character* c = canvas->GetCharactersBuffer();
        Character* e = c + ((size_t) canvasWidth * (size_t) canvasHeight);

        while (c < e) {
            c->PackedValue = *stepVal;
            ++stepVal;
            c++;
        }
    }

    void OnButtonPressed(Reference<Controls::Button> r) override
    {
        bool goBack = r->GetControlID() == 100;
        if (goBack) {
            if (currentTutorialStep > 0) {
                currentTutorialStep--;
                nextButton->SetEnabled(true);
                if (currentTutorialStep == 0) {
                    backButton->SetEnabled(false);
                }
                PrintCurrentStep();
            }
        } else { // forward
            if (currentTutorialStep < tutorialSteps.size() - 1) {
                currentTutorialStep++;
                backButton->SetEnabled(true);
                if (currentTutorialStep == tutorialSteps.size() - 1) {
                    nextButton->SetEnabled(false);
                }
                PrintCurrentStep();
            }
        }
    }
};

void Instance::ShowTutorial()
{
    TutorialWindow tutorial{};
    tutorial.Show();
}